import React, { useState } from "react";
import axios from "axios";

const TranslateUI = () => {
  const [text, setText] = useState("");
  const [lang, setLang] = useState("hindi");
  const [result, setResult] = useState("");

  const handleTranslate = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8080/translate", {
        text,
        target_lang: lang,
      });
      setResult(res.data.translation);
    } catch (err) {
      alert("Error translating: " + err.message);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>English to Hindi/Bengali Translator</h2>
      <form onSubmit={handleTranslate}>
        <textarea
          rows="4"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter English text..."
          style={{ width: "100%", padding: "10px" }}
        />
        <br />
        <select value={lang} onChange={(e) => setLang(e.target.value)} style={{ margin: "10px 0" }}>
          <option value="hindi">Hindi</option>
          <option value="bengali">Bengali</option>
        </select>
        <br />
        <button type="submit">Translate</button>
      </form>
      {result && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h4>Translation:</h4>
          <p>{result}</p>
        </div>
      )}
    </div>
  );
};

export default TranslateUI;
