import React, { useState } from "react";
import axios from "axios";

function TranslateSummarizeUI() {
  const [text, setText] = useState("");
  const [lang, setLang] = useState("hindi");
  const [summary, setSummary] = useState("");
  const [translation, setTranslation] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSummary("");
    setTranslation("");

    if (!text.trim()) {
      setError("Please enter some text.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:8080/summarize", {
        text,
      });
      const summaryText = res.data.summary;
      setSummary(summaryText);

      const transRes = await axios.post("http://localhost:8080/translate", {
        text: summaryText,
        target_lang: lang,
        console
      });
      setTranslation(transRes.data.translation);
    } catch (err) {
      setError("An error occurred: " + err.message);
    }
  };

  return (
    <div className="container mx-auto mt-10 px-4">
      <h1 className="text-center text-2xl font-bold text-gray-800">
        Combined Summarizer & Translator
      </h1>
      <p className="text-center text-gray-500">
        Generate a summary of your English text and translate it to Hindi/Bengali.
      </p>

      <div className="bg-white shadow-md rounded-lg p-6 mt-6">
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="inputText" className="block text-gray-700 font-medium">
              Enter your English text
            </label>
            <textarea
              className="w-full mt-2 border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              id="inputText"
              rows="5"
              placeholder="Paste your text here..."
              value={text}
              onChange={(e) => setText(e.target.value)}
            ></textarea>
          </div>

          <div className="mb-4">
            <label className="block text-gray-700 font-medium">Choose Language</label>
            <select
              value={lang}
              onChange={(e) => setLang(e.target.value)}
              className="w-full mt-2 border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="hindi">Hindi</option>
              <option value="bengali">Bengali</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-900 transition duration-300"
          >
            Summarize & Translate
          </button>
        </form>
      </div>

      {(summary || translation || error) && (
        <div className="mt-8">
          <h3 className="text-center text-xl font-semibold text-gray-800">Results</h3>
          <div className="bg-white shadow-md rounded-lg p-6 mt-4">
            {error && <p className="text-red-500 mb-4">{error}</p>}

            {summary && (
              <>
                <p className="font-semibold text-gray-700">Summary:</p>
                <p className="text-gray-600">{summary}</p>
                <hr className="my-4" />
              </>
            )}

            {translation && (
              <>
                <p className="font-semibold text-gray-700">
                  Translated Text ({lang}):
                </p>
                <p className="text-gray-600">{translation}</p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default TranslateSummarizeUI;
