import React, { useState } from "react";
import axios from "axios";

const TranslateSummarizeUI = () => {
  const [text, setText] = useState("");
  const [lang, setLang] = useState("hindi");
  const [summary, setSummary] = useState("");
  const [translation, setTranslation] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSummary("");
    setTranslation("");

    try {
      const res = await axios.post("http://localhost:8080/summarize", {
        text,
      });
      const summaryText = res.data.summary;
      setSummary(summaryText);

      const transRes = await axios.post("http://localhost:8080/translate", {
        text: summaryText,
        target_lang: lang,
      });
      setTranslation(transRes.data.translation);
    } catch (err) {
      setError("An error occurred: " + err.message);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "auto" }}>
      <h2>English Text Summarizer & Translator (Hindi/Bengali)</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          rows="5"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter English text..."
          style={{ width: "100%", padding: "10px" }}
        />
        <br />
        <select
          value={lang}
          onChange={(e) => setLang(e.target.value)}
          style={{ margin: "10px 0" }}
        >
          <option value="hindi">Hindi</option>
          <option value="bengali">Bengali</option>
        </select>
        <br />
        <button type="submit">Summarize & Translate</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {summary && (
        <div style={{ marginTop: "20px" }}>
          <h4>Summary:</h4>
          <p>{summary}</p>
        </div>
      )}

      {translation && (
        <div style={{ marginTop: "20px" }}>
          <h4>Translation ({lang}):</h4>
          <p>{translation}</p>
        </div>
      )}
    </div>
  );
};

export default TranslateSummarizeUI;
