import React, { useState } from 'react';

const Summarization = () => {
  const [inputText, setInputText] = useState('');
  const [summarizedText, setSummarizedText] = useState('');
  const [error, setError] = useState('');

  const handleSummarize = async () => {
    try {
      // Send the POST request to the Flask backend
      const response = await fetch('http://localhost:5000/summarize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: inputText }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch summary');
      }

      const data = await response.json();

      // Set the summarized text in state
      setSummarizedText(data.summarized_text);
      setError('');  // Clear any previous errors
    } catch (error) {
      // Set the error in state if something goes wrong
      setSummarizedText('');
      setError(error.message);
    }
  };

  return (
    <div>
      <h1>Text Summarization</h1>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        placeholder="Enter text to summarize"
      ></textarea>
      <button onClick={handleSummarize}>Summarize</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {summarizedText && <p>Summarized Text: {summarizedText}</p>}
    </div>
  );
};

export default Summarization;
